import { useState } from "react";




const Reset = () => {
    
    const [count, setCount] = useState(0);

    const handleIncrement = () => {
        setCount( count + 1);
    };
    
    const handleReset = () => {
        handleIncrement = () => {
            setCount( count = 0 );
        }
    } 

    return (
        <button className="btn" onClick={ handleReset }>
            reset!
        </button>
    );
}


export default Reset;
