import { useState } from "react";
import ButtonIncrement from "./ButtonIncrement";
import setCount from "./ButtonIncrement";
import count from "./ButtonIncrement";


const Reset = () => {

    const [count, setCount] = useState(0);

    const handleReset = () => {
        ButtonIncrement.handleIncrement = () => {
            setCount( count = 0 );
        }
    } 

    return (
        <button className="btn" onClick={ handleReset }>
            reset!
        </button>
    );
}

export default Reset;
